import { useState } from 'react';
import './App.css';
import Header from './components/header/Header';
import Tasks from './components/tasks/Tasks';


function App() {
  const[tasks,setTasks] = useState([{id:1 ,name:'wang'},

  {id:2,name:'Yike'}]);

  //delete task

  const deleteTask =(id) =>{
    setTasks(tasks.filter((task)=>(task.id !== id)))//new array
  }
  return (
    <div className="App">
     <Header title='Hello' />

     {tasks.length>0?<Tasks tasks={tasks} onDelete ={deleteTask}/> :'No T' }
    </div>

  );
}

export default App;
